<?php	/*	Template Name: contact	*/?><?php	get_header('single');?><?php //If the form is submittedif(isset($_POST['submitted'])) {	//Check to see if the honeypot captcha field was filled in	if(trim($_POST['checking']) !== '') {		$captchaError = true;	} else {			//Check to make sure that the name field is not empty		if(trim($_POST['contactName']) === '') {			$nameError = 'Indiquez votre nom.';			$hasError = true;		} else {			$name = trim($_POST['contactName']);		}				//Check to make sure sure that a valid email address is submitted		if(trim($_POST['email']) === '')  {			$emailError = 'Indiquez une adresse e-mail valide.';			$hasError = true;		} else if (!preg_match("/^[[:alnum:]][a-z0-9_.-]*@[a-z0-9.-]+\.[a-z]{2,4}$/i", trim($_POST['email']))) {			$emailError = 'Adresse e-mail invalide.';			$hasError = true;		} else {			$email = trim($_POST['email']);		}					//Check to make sure comments were entered			if(trim($_POST['comments']) === '') {			$commentError = 'Entrez votre message.';			$hasError = true;		} else {			if(function_exists('stripslashes')) {				$comments = stripslashes(trim($_POST['comments']));			} else {				$comments = trim($_POST['comments']);			}		}					//If there is no error, send the email		if(!isset($hasError)) {			$emailTo = 'samu-emmanuel@hotmail.com';			$subject = 'Formulaire de contact de '.$name;			$body = "Name: $name \n\nEmail: $email \n\nComments: $comments";			$headers = 'De : es <'.$emailTo.'>' . "\r\n" . 'R&eacute;pondre &agrave; : ' . $email;						mail($emailTo, $subject, $body, $headers);			$emailSent = true;		}	}} ?><section id="contact">				<div class="info_contact vcard">					<h3><em>Mes</em> infos</h3>						<?php the_meta(); ?>										<p class="post-reseaux-sociaux">						<?php 						$args = array('post_type' => 'social');						$loop = new WP_Query( $args );						while ( $loop->have_posts() ) : $loop->the_post();						foreach (get_post_meta($post->ID) as $key=>$value) : 							if( $key[0] != '_') { ?>							<a href="<?php echo $value[0]; ?>" title="Rejoins moi sur <?php echo $key; ?>">								<?php the_post_thumbnail(); ?>							</a>															 						<?php	}							endforeach; 						endwhile; ?>													</p>					</div><div id="form"><?php if(isset($emailSent) && $emailSent == true) { ?>		<h3>Contactez&nbsp;<em>-&nbsp;moi</em></h3>	<div class="thanks">		<p>Merci, <?php echo $name; ?>.</p>		<p>Votre message a &eacute;t&eacute; envoy&eacute; avec succ&egrave;s.</p>	</div><?php } else { ?>	<?php if (have_posts()) : ?>		<?php while (have_posts()) : the_post(); ?>	<h3>Contactez&nbsp;<em>-&nbsp;moi</em></h3>				<?php if(isset($hasError) || isset($captchaError)) { ?>			<p class="error">Un des champs n'a pas &eacute;t&eacute; rempli</p>		<?php } ?>			<form action="<?php the_permalink(); ?>" id="contactForm" method="post">				<ol class="forms">				<li>					<?php if($nameError != '') { ?>						<span class="error"><?=$nameError;?></span> 					<?php } ?>					<label for="contactName">Nom</label>					<input type="text" name="contactName" id="contactName" value="<?php if(isset($_POST['contactName'])) echo $_POST['contactName'];?>" class="requiredField" />									</li>								<li>					<?php if($emailError != '') { ?>						<span class="error"><?=$emailError;?></span>					<?php } ?>					<label for="email">E-mail</label>					<input type="text" name="email" id="email" value="<?php if(isset($_POST['email']))  echo $_POST['email'];?>" class="requiredField email" />									</li>								<li class="textarea"><label for="commentsText">Message</label>					<textarea name="comments" id="commentsText" rows="8" cols="30" class="requiredField"><?php if(isset($_POST['comments'])) { if(function_exists('stripslashes')) { echo stripslashes($_POST['comments']); } else { echo $_POST['comments']; } } ?></textarea>					<?php if($commentError != '') { ?>						<span class="error"><?=$commentError;?></span> 					<?php } ?>				</li>								<li class="buttons"><input type="hidden" name="submitted" id="submitted" value="true" /><input type="submit" value="Envoyer"></li>			</ol>		</form>			<?php endwhile; ?>	<?php endif; ?><?php } ?></div></section><?php get_footer(); ?>